/**
 *
 * @param {import("atomico").Ref} ref
 * @param {(coordinates:Coordinates)=>void} callback
 */
export function useClickCoordinates(ref: import("atomico").Ref, callback: (coordinates: Coordinates) => void): void;
/**
 *
 * @param {{pageX:number,pageY:number, currentTarget:EventTarget|null}} event
 * @returns {Coordinates}
 */
export function getCoordinates({ pageX: x, pageY: y, currentTarget }: {
    pageX: number;
    pageY: number;
    currentTarget: EventTarget | null;
}): Coordinates;
export type Coordinates = {
    x: number;
    y: number;
    offset: {
        x: number;
        y: number;
    };
};
