/**
 * this hook replaces a property with a setter and
 * getter to observe these mutations
 * @param {string} prop
 * @param { { set?: (value: any) => any, get?: () => any }} config
 */
export function usePropProxy(prop: string, config: {
    set?: (value: any) => any;
    get?: () => any;
}): void;
